import React from 'react';

const About = () => {
    return (
        <div>
            <h1>About</h1>
            <p>This is going to be an application using the breweryDB API. I know that me and many others, love beer. Users will be able to search breweries that are local to them(Or anywhere in the US of A), and see what kind of beers that they offer. I was originally going to create this App using strictly front end technologies, but I am going to store my api key on the backend. For the backend the technologies I am using are express.js and node.js</p>
        </div>
    );
};

export default About;
