import React from 'react';

const About = () => {
    return (
        <div>
            <h1>About</h1>
            <p>This is going to be an application using the API breweryDB. I know that me and many others, love beer. Users will be able to search breweries that are local to them, and see what kind of beers that they offer. For right now, this is going to be a front end application, but future versions may allow users to create accounts and give ratings to the beers and brewery.</p>
        </div>
    );
};

export default About;
